import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Modal from './Modal'; // Import the Modal component
import './Signup.css'; // Import CSS for styling

function Signup() {
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [repeatPassword, setRepeatPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalTitle, setModalTitle] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    let validationErrors = {};
    if (!firstname) validationErrors.firstname = 'First name is required';
    if (!lastname) validationErrors.lastname = 'Last name is required';
    if (!email) validationErrors.email = 'Email is required';
    if (!password) validationErrors.password = 'Password is required';
    if (password !== repeatPassword) validationErrors.repeatPassword = 'Passwords do not match';

    setErrors(validationErrors);
    if (Object.keys(validationErrors).length > 0) return;

    try {
      await axios.post('http://localhost:5000/signup', { firstname, lastname, email, password });
      setModalTitle('Success');
      setModalMessage('Account created');
      setModalIsOpen(true);
      navigate('/login');
    } catch (error) {
      if (error.response && error.response.data === 'Email already exists') {
        setModalTitle('Error');
        setModalMessage('Account already exists');
        setModalIsOpen(true);
      } else {
        alert('Error creating account');
      }
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={firstname}
            onChange={(e) => setFirstname(e.target.value)}
            placeholder="First Name"
            required
          />
          {errors.firstname && <p className="error">{errors.firstname}</p>}
        </div>
        <div>
          <input
            type="text"
            value={lastname}
            onChange={(e) => setLastname(e.target.value)}
            placeholder="Last Name"
            required
          />
          {errors.lastname && <p className="error">{errors.lastname}</p>}
        </div>
        <div>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email Address"
            required
          />
          {errors.email && <p className="error">{errors.email}</p>}
        </div>
        <div>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          {errors.password && <p className="error">{errors.password}</p>}
        </div>
        <div>
          <input
            type="password"
            value={repeatPassword}
            onChange={(e) => setRepeatPassword(e.target.value)}
            placeholder="Repeat Password"
            required
          />
          {errors.repeatPassword && <p className="error">{errors.repeatPassword}</p>}
        </div>
        <button type="submit">Signup</button>
      </form>
      
      {/* Modal for displaying account exists error */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        title={modalTitle}
        message={modalMessage}
      />
    </>
  );
}

export default Signup;
