import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Modal from './custom/Modal'; // Import the Modal component

function Signup() {
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [repeatPassword, setRepeatPassword] = useState('');
  const [firstnameError, setFirstnameError] = useState('');
  const [lastnameError, setLastnameError] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [repeatPasswordError, setRepeatPasswordError] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalTitle, setModalTitle] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Reset error states
    setFirstnameError('');
    setLastnameError('');
    setEmailError('');
    setPasswordError('');
    setRepeatPasswordError('');

    let hasError = false;

    if (!firstname) {
      setFirstnameError('First Name is required');
      hasError = true;
    }

    if (!lastname) {
      setLastnameError('Last Name is required');
      hasError = true;
    }

    if (!email) {
      setEmailError('Email Address is required');
      hasError = true;
    }

    if (!password) {
      setPasswordError('Password is required');
      hasError = true;
    }

    if (password !== repeatPassword) {
      setRepeatPasswordError('Passwords do not match');
      hasError = true;
    }

    if (hasError) {
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/signup', { firstname, lastname, email, password });
      setModalTitle('Success');
      setModalMessage('Account created');
      setModalIsOpen(true);
      navigate('/login');
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        const errorMsg = error.response.data.error;
        if (errorMsg.includes('Email')) {
          setEmailError(errorMsg);
        } else {
          setModalTitle('Error');
          setModalMessage(errorMsg);
          setModalIsOpen(true);
        }
      } else {
        setModalTitle('Error');
        setModalMessage('An error occurred. Please try again.');
        setModalIsOpen(true);
      }
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={firstname}
            onChange={(e) => setFirstname(e.target.value)}
            placeholder="First Name"
            required
          />
          {firstnameError && <p className="error">{firstnameError}</p>}
        </div>
        <div>
          <input
            type="text"
            value={lastname}
            onChange={(e) => setLastname(e.target.value)}
            placeholder="Last Name"
            required
          />
          {lastnameError && <p className="error">{lastnameError}</p>}
        </div>
        <div>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email Address"
            required
          />
          {emailError && <p className="error">{emailError}</p>}
        </div>
        <div>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
          {passwordError && <p className="error">{passwordError}</p>}
        </div>
        <div>
          <input
            type="password"
            value={repeatPassword}
            onChange={(e) => setRepeatPassword(e.target.value)}
            placeholder="Repeat Password"
            required
          />
          {repeatPasswordError && <p className="error">{repeatPasswordError}</p>}
        </div>
        <button type="submit">Signup</button>
      </form>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        title={modalTitle}
        message={modalMessage}
      />
    </>
  );
}

export default Signup;
