import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Modal from './Modal'; // Import the Modal component

function Signup() {
  const [firstname, setFirstname] = useState('');
  const [lastname, setLastname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [repeatPassword, setRepeatPassword] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalTitle, setModalTitle] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== repeatPassword) {
      setModalTitle('Error');
      setModalMessage('Passwords do not match');
      setModalIsOpen(true);
      return;
    }

    try {
      await axios.post('http://localhost:5000/signup', { firstname, lastname, email, password, repeatPassword });
      setModalTitle('Success');
      setModalMessage('Account created');
      setModalIsOpen(true);
      navigate('/login');
    } catch (error) {
      setModalTitle('Error');
      setModalMessage('Email already exists');
      setModalIsOpen(true);
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input type="text" value={firstname} onChange={(e) => setFirstname(e.target.value)} placeholder="First Name" required />
        <input type="text" value={lastname} onChange={(e) => setLastname(e.target.value)} placeholder="Last Name" required />
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email Address" required />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
        <input type="password" value={repeatPassword} onChange={(e) => setRepeatPassword(e.target.value)} placeholder="Repeat Password" required />
        <button type="submit">Signup</button>
      </form>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        title={modalTitle}
        message={modalMessage}
      />
    </>
  );
}

export default Signup;
