import { createBrowserRouter, Route, Navigate } from 'react-router-dom';
import React from 'react';
import {
  AboutPage,
  ContactPage,
  LandingPagesection,
  NotFoundpage,
} from '../pages/index.js';
import Signup from '../components/Signup.jsx';
import Login from '../components/Login.jsx';

const isAuthenticated = () => {
  const token = localStorage.getItem('token');
  return token ? true : false;
};

const ProtectedRoute = ({ path, element }) => {
  return isAuthenticated() ? <Route path={path} element={element} /> : <Navigate to="/login" />;
};

const routes = [
  {
    path: '/',
    element: <Navigate to="/LandingPage" replace />,
  },
  {
    path: '/LandingPage',
    element: <LandingPagesection />,
  },
  {
    path: '/About',
    element: <ProtectedRoute path="/About" element={<AboutPage />} />,
  },
  {
    path: '/Contact',
    element: <ProtectedRoute path="/Contact" element={<ContactPage />} />,
  },
  {
    path: '/Signup',
    element: <Signup />,
  },
  {
    path: '/Login',
    element: <Login />,
  },
  {
    path: '*',
    element: <NotFoundpage />,
  },
];

const Router = createBrowserRouter(routes);

export default Router;
